#![allow(non_snake_case)]

#[cfg(test)]
mod tests {
    use hex;
    use reed_solomon_simd::{ReedSolomonEncoder};
    use serde_json::json;
    use std::error::Error;
    use std::fs;

    fn encode_and_save_json(
        d_hex: &str,
        V: usize,
        C: usize
    ) -> Result<(), Box<dyn std::error::Error>> {
        let W_E = C * 2;
        let mut d_bytes = hex::decode(d_hex.trim_start_matches("0x"))?;
        let d_bytes_len = d_bytes.len();
        if d_bytes.len() % W_E != 0 {
            let pad_len = W_E - (d_bytes.len() % W_E);
            d_bytes.extend(std::iter::repeat(0).take(pad_len));
        }

        let shard_size = d_bytes.len() / C;
        let k = d_bytes.len() / W_E;
        let mut original_shards: Vec<Vec<u8>> = vec![Vec::with_capacity(2 * k); C];
        let mut recovery_shards: Vec<Vec<u8>> = vec![Vec::with_capacity(2 * k); V - C];

        let mut encoder = ReedSolomonEncoder::new(C, V - C, shard_size)?;
        for i in 0..C {
            let shard: Vec<u8> = d_bytes[i * shard_size..(i + 1) * shard_size].to_vec();
            encoder.add_original_shard(&shard)?;
            original_shards[i].extend_from_slice(&shard);
        }
        let encoded = encoder.encode()?;
        for (j, shard) in encoded.recovery_iter().enumerate() {
                recovery_shards[j].extend_from_slice(shard);
        }

        let output_json = json!({
            "data": d_hex,
            "shards": original_shards
                .iter()
                .chain(recovery_shards.iter())
                .map(|s| format!("0x{}", hex::encode(s)))
                .collect::<Vec<String>>(),
        });

        
        let fn_ = format!("ec-{}.json", d_bytes_len);
        fs::write(&fn_, serde_json::to_string_pretty(&output_json)?)?;
       
        Ok(())
    }

    #[test]
    fn test_encode() -> Result<(), Box<dyn Error>> {
        let inputs = [
            "0x615d17",
            "0x1421199addac7c87873a",
            "0xea5e4aafeb04ca7e9a8df05777c343056e02b55ac79074db59c94b46e64373d8",
            "0x6899c10632848450fc4daae93d07ea10c10c96434355ae1a20e7fa109f130418fa10acb8e790d710f79614ddff2b7d66a804fcd0d32046a0c39caf301288501d98f635ed7d28be53e108793f79353267dcfb3545eb1fe946c057accb24156c2108eb530a",
            "",
            "",
            "",
            ""
        ];

        for &input in &inputs {
            encode_and_save_json(input, 6, 2)?;
        }

        Ok(())
    }

}
